import React from 'react';
import {Box,Container,Grid} from '@material-ui/core';
import HostelCard from './HostelCard'
import hs1 from 'assets/image/hostel.jpg'
import axios from 'axios'
export default function DisableElevation() {
  const data = [
    {
      name: 'Valeriia',
      price: 786,
      desc: 'A hostel is a large house where people can stay cheaply for a short period of time. ',
      img:hs1
    },
    {
      name: 'Valeriia2',
      price: 800,
      desc: 'A hostel is a large house where people can stay cheaply for a short period of time. ',
      img:hs1
    },
    {
      name: 'Valeriia3',
      price: 1006,
      desc: 'A hostel is a large house where people can stay cheaply for a short period of time. ',
      img:hs1
    },
    {
      name: 'Valeriia4',
      price: 1800,
      desc: 'A hostel is a large house where people can stay cheaply for a short period of time. ',
      img:hs1
    },
  ]

  const [data1, setData] = React.useState([]);
  React.useEffect(() => {
    const fetchData = async () => {
      const result = await axios.get(
        "https://8a2fc1a9-0d10-4a86-8098-56ddb92a968a.mock.pstmn.io/hostel",
      );

      setData(result.data);
    };

    fetchData();
  }, []);

  const listItems = data.map((hostel) =>
    <Grid item xs={12} sm={6} md={4} lg={3}>
  <HostelCard name={hostel.name} price={hostel.price} desc={hostel.desc} img={hostel.img} />
    </Grid>

);


  return (
    <Container fixed>
      {data1}
      <Box fontSize={24} fontWeight={700} paddingTop={8} paddingBottom={ 4}>
        List
      </Box>
      <Grid container justify='center' alignItems='center' spacing={2}>
      {listItems}
      </Grid>

      </Container>
  );
}
