import axios from 'axios'

const LOAD_LOGIN_REQUEST = 'hostel_management/src/ui/LOAD_LOGIN_REQUEST'
const LOAD_LOGIN_SUCCESS = 'hostel_management/src/ui/LOAD_LOGIN_SUCCESS'
const LOAD_LOGIN_FAILURE = 'hostel_management/src/ui/LOAD_LOGIN_REQUEST'
const LOGIN_CHECK = 'hostel_management/src/ui/LOGIN_CHECK'
const SET_LOGIN = 'hostel_management/src/ui/SET_LOGIN'

function loadLogin(loginInfo) {
  return async (dispatch) => {
    dispatch({ type: LOAD_LOGIN_REQUEST })
    try {
      const { data } = await axios.post(`https://8a2fc1a9-0d10-4a86-8098-56ddb92a968a.mock.pstmn.io/book/${loginInfo.email}&&${loginInfo.password}`)
      dispatch({
        type: LOAD_LOGIN_SUCCESS,
        payload: {
          token: data,
        },
      })
      dispatch({
        type: SET_LOGIN,
        payload: {
            login:true,
          },
      })
    } catch (err) {
      dispatch({ type: LOAD_LOGIN_FAILURE })
    }
  }
}



export {
  LOAD_LOGIN_REQUEST,
  LOAD_LOGIN_SUCCESS,
  LOAD_LOGIN_FAILURE,
  LOGIN_CHECK,
  SET_LOGIN,
  loadLogin,
}
