import React from 'react'
import { useForm } from 'react-hook-form'
import { makeStyles } from '@material-ui/core/styles'
import { AiOutlineUser } from 'react-icons/ai'
import { RiLockPasswordLine } from 'react-icons/ri'


import {
  Container,
  InputAdornment,
  Button,
  CssBaseline,
  TextField,
  Grid,
  Link,
  Typography,
} from '@material-ui/core'
import { NavLink } from 'react-router-dom'


import * as yup from 'yup'

const useStyles = makeStyles((theme) => ({
  input: {
    color: '#0f1626',
    fontWeight: 700,
    '& .MuiInputBase-root.MuiOutlineInput-root': {
      color: '#45A29E',
      borderColor: '#757575',
      fontWeight: 700,
    },
  },
  textfield: {
    marginTop: 10,

    '& .MuiFormHelperText-root.Mui-error ': {
      color: 'ff533d',
      fontWeight: 700,
      borderWidth: '3px',
    },
    '& .MuiInput-underline.Mui-error:after': {
      borderColor: 'ff533d',
      borderWidth: '3px',
    },
    '& label.MuiFormLabel-root': {
      fontWeight: 700,
      '&:after .Mui-error': {
        borderColor: '#ff533d',
        borderWidth: '3px',
      },
    },
    '& label.Mui-focused': {
      color: '#132740',
    },
    '& .MuiInput-underline:after': {
      borderBottomColor: '#ffae0c',
    },
    '& .MuiOutlinedInput-root': {
      borderWidth: '3px',
      '& fieldset': {
        borderColor: '#ffae0c',
        borderWidth: '2px',
      },
      '&:hover fieldset': {
        borderColor: '#a8c6ff',
        borderWidth: '3px',
      },
      '&.Mui-focused fieldset': {
        borderColor: '#a8c6ff',
        borderWidth: '3px',
      },
      '&.Mui-error .MuiOutlinedInput-notchedOutline': {
        borderColor: '#ff533d',
        borderWidth: '3px',
      },
    },
  },
  paper: {
    marginTop: theme.spacing(4),
    display: 'flex',
    padding: 10,
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    width: theme.spacing(7),
    height: theme.spacing(7),

    backgroundColor: theme.palette.secondary.main,
  },

  submit: {
    marginTop: '10px',
    background: '#ff533dcf',
    borderRadius: 20,
    padding: 8,
    color: '#fdfdfd',
    '&:hover': {
      background: '#ff533d',
    },
  },
  etda: {
    marginTop: '10px',
    background: '#4d74bbba',
    borderRadius: 20,
    padding: 8,
    color: '#fdfdfd',
    '&:hover': {
      background: '#4d74bb',
    },
  },
  container: {
    display: 'flex',
    alignItems: 'center',
  },

  border: {
    borderBottom: '1px solid darkgray',
    width: '100%',
  },

  content: {
    padding: ' 0 10px 0 10px',
  },
  divider: {
    marginTop: '20px',
  },
  textdivider: {
    color: 'royalblue',
  },
  nav: {
    color: 'inherit',
    textDecoration: 'inherit',
  },
  form: {
    background: 'white',
    borderRadius: '10px',
    height: '100%',
    marginTop: '20px',
  },
  font: {
    fontWeight: 700,
    color: '#0f1726',
  },
  image: {
    margin: 10,
    display: 'block',
    marginLeft: 'auto',
    marginRight: 'auto',
    width: '50%',
  },
}))

export default function SignIn() {
  const classes = useStyles()
  const { register, handleSubmit, errors } = useForm({
    mode: 'onSubmit',
    validationSchema: yup.object().shape({
      user: yup
        .string()
        .required('กรุณากรอกเลขบัตรประจำตัวประชาชน')
        .matches(/^[0-9]{13}$/, 'กรุณากรอกเป็นตัวเลข 13 หลัก')
        .test(
          'ตรวจสอบรหัสบัตรประชาชน',
          'กรอกเลขบัตรประชาชนผิด กรุณากรอกใหม่',

          function (item) {
            var i, sum
            for (i = 0, sum = 0; i < 12; i++)
              sum += parseFloat(item.charAt(i)) * (13 - i)
            if ((11 - (sum % 11)) % 10 !== parseFloat(item.charAt(12))) {
              return false
            }
            return true
          },
        ),
      password: yup.string().required(),
    }),
  })

  const onLogin = (loginInfo) => {
    console.log(loginInfo)
    const action = actions.setLogin(true)
    dispatch(action)
    history.push(`/learning-portal`)
  }

  return (
    <Container component="main" maxWidth="xs">
      <form autoComplete="off" className={classes.form}>
        <CssBaseline />
        <div className={classes.paper}>
          <img alt="banner" src={img} className={classes.image} />
          <Typography component="h1" variant="h5" className={classes.font}>
            Learning Portal
          </Typography>
          <form className={classes.form} noValidate>
            <TextField
              label="เลขประจำตัวประชาชน"
              name="user"
              inputRef={register}
              variant="outlined"
              className={classes.textfield}
              InputProps={{
                className: classes.input,
                startAdornment: (
                  <InputAdornment position="start">
                    <AiOutlineUser size={24} />
                  </InputAdornment>
                ),
              }}
              fullWidth
              helperText={errors.user ? 'กรุณากรอกรหัสผู้ใช้' : ''}
              error={!!errors.user}
            />
            <TextField
              className={classes.textfield}
              label="รหัสผ่าน"
              type="password"
              name="password"
              inputRef={register}
              fullWidth
              variant="outlined"
              helperText={errors.password ? 'กรุณากรอกรหัสผ่าน' : ''}
              error={!!errors.password}
              InputProps={{
                className: classes.input,
                startAdornment: (
                  <InputAdornment position="start">
                    <RiLockPasswordLine size={24} />
                  </InputAdornment>
                ),
              }}
            />

            <Button
              type="submit"
              fullWidth
              className={classes.submit}
              onClick={handleSubmit(onLogin)}
            >
              เข้าสู่ระบบ
            </Button>

            <Grid container>
              <Grid item xs>
                <NavLink to="/learning-portal/forget" className={classes.nav}>
                  <Link variant="body2">ลืมรหัสผ่าน?</Link>
                </NavLink>
              </Grid>
              <Grid item>
                <NavLink to="/learning-portal/signup" className={classes.nav}>
                  <Link variant="body2">{'สมัครสมาชิก'}</Link>
                </NavLink>
              </Grid>
            </Grid>
            <div className={classes.container}>
              <div className={classes.border} />
              <span className={classes.content}>
                <h3 className={classes.textdivider}>OR</h3>
              </span>
              <div className={classes.border} />
            </div>

            <Button fullWidth className={classes.etda}>
              LOGIN WITH ETDA CONNECT
            </Button>
          </form>
        </div>
      </form>
    </Container>
  )
}
