import React from 'react';
import { Box, Container,Grid,Button } from '@material-ui/core';
import { useLocation } from 'react-router-dom'
import axios from 'axios'
import { useDispatch,useSelector } from 'react-redux'
import * as actions from '../action'
import { useHistory } from 'react-router-dom'
export default function Card() {
  const  {search}  = useLocation()


  const [data, setData] = React.useState([]);

  React.useEffect(() => {

    const fetchData = async () => {
      const result = await axios.get(
        `https://8a2fc1a9-0d10-4a86-8098-56ddb92a968a.mock.pstmn.io/hostel${search}`,
      );

      setData(result.data);
    };

    fetchData();
  }, []);
  const history = useHistory()
  const dispatch = useDispatch()
  const productIds = useSelector((state) => state.hostel.productIds)
  const addCart = () => { 
    const id=data.id
    if (id != undefined) {
      const action = actions.addToCart(id)
      dispatch(action)

    }

    {console.log(productIds)}
  }
  const bookNow = () => {
    addCart()
    history.push('/book')
  }


  return (
    <>

            <Container fixed>
              <Box fontSize={24} fontWeight={700} marginTop={ 8}>
                  Detail
          </Box>
              <Grid container justify='flex-start' alignItems='center' >
                  <Grid items xs={12} sm={ 4}>
                  <img src={data.img} alt="hostel" width='100%'  style={{borderRadius:10}}/> 
                  </Grid> 
                  <Grid items xs={12} sm={8} >
                  <Box marginLeft={ 8}>
                  <Box fontSize={24} fontWeight={700} >
                  {data.name}
          </Box>
          <Box fontSize={18} fontWeight={700} >
          {data.desc}
          </Box>
          <Box fontSize={18} fontWeight={700} >
          {data.price}
          </Box>            
          <Button onClick={bookNow}>Book</Button>
          </Box>

                      </Grid>
              </Grid>

          </Container>
    </>
  );
}
