import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import {Box} from '@material-ui/core';
import { Link as RouterLink, useHistory, NavLink } from 'react-router-dom'
import currencyFormat from 'utils/currencyFormat'

const useStyles = makeStyles({
  root: {
    maxWidth: 345,
    marginTop:10
  },
});

export default function ImgMediaCard(props) {
  const classes = useStyles();
  const { name, price, desc, img } = props
  const history = useHistory()
  const navigatorToDeatail = () => {
    history.push(`/hostel?${name}`)
  }
  return (
    <Card className={classes.root}>

        <CardMedia
          component="img"
          alt="Contemplative Reptile"
          height="200"
          image={img}
          title="Contemplative Reptile"
        />
        <CardContent>
          <Box fontSize={24} fontWeight={ 700}>
      {name}
          </Box>
          <Box fontSize={16} fontWeight={ 400}>
           {desc}
          </Box>
        </CardContent>

      <CardActions  style={{float:'right',paddingRight:14}}>



        <Box fontSize={18} fontWeight={700} onClick={navigatorToDeatail}>
  {      currencyFormat(price)}
               </Box>




      </CardActions>
    </Card>
  );
}
